<script setup>
import { defineProps, ref } from 'vue';
import serverURL from '../router/serverAddress'

const props = defineProps({
    userImages: {
        type: Number,
        required: true,
    },
});

// file input
const openFileInput = () => {
    const imageInput = document.querySelector('input[name=fileInput]')
    imageInput.click()
}
const userImages = ref([])
const filename = ref([])
const file = ref([])
// set image data
const setNewImage = (e) => {
    if (e.target.files.length > 3) {
        console.log('end');
        return;
    }
    if (e.target.files.length == 3) {
        userImages.value = []
        file.value = []
        filename.value = []
    }
    if (userImages.value.length >= 3) {
        userImages.value = []
        file.value = []
        filename.value = []
    }
    const files = []
    for (let i = 0; i < e.target.files.length; i++) {
        files.push(e.target.files[i])
    }
    files.forEach(item => {
        const reader = new FileReader();
        reader.onload = (event) => {
            userImages.value.push(event.target.result);
        };
        reader.readAsDataURL(item);

        // Prompt the user to enter a new file name
        const newFileName = "user_" + localStorage.getItem('userId') + "_" + Date.now();
        // Change the file name of the selected file
        // item.name = newFileName;
        filename.value.push(item.name);

        // Get file name and file itself to send to the database
        const selectedFile = item;
        file.value.push(selectedFile);

    });
    console.log(file.value);
    console.log(filename.value);
};
