<script setup>
import { RouterLink } from 'vue-router'
import { ref , onBeforeMount , computed } from 'vue'
import serverURL from '../router/serverAddress'
import AuthService from "../services/auth.service";


const userData = ref(null)
const username = ref(null)
const phone = ref(null)
const hasImage = ref(false)
const successful = ref(false)
const phonenumberholder = ref(null)
const usernameholder = ref(null)
const usernameinput = ref(null)
const IsSetNewUsername = ref(false)
const loading = ref(false)
const errorMessage = ref(null)
const currentUser = computed(() =>{
  return store.state.auth.auth.user;
})

onBeforeMount(()=>{
  if (!currentUser) {
    router.push('/login');
  }
  else{
    async function getData () {
      await fetch(serverURL + "/api/users/" + localStorage.getItem('userId'))
      .then(res => res.json())
      .then(data => {
        userData.value = data;
        username.value = userData.value.username;
        phone.value = userData.value.phone
      })
    }
    getData()
  }
})

const openFileInput = (e) =>{
    // const imageInput = document.querySelector('input[type=file]')
    // imageInput.click()
}

const setNewImage = (e) =>{
    let reader = new FileReader
    reader.onload = e => {
      userData.value.image = e.target.result
    }
    reader.readAsDataURL(e.target.files[0])
}

const authorizeUsername = () =>{
  loading.value = true
  if(userData.value.username != ''){
    updateUsername()
  }
  loading.value = false
}

async function updateUsername(user) {
  await AuthService.generateNewToken()
  const token = localStorage.getItem('token')
  const data = {
    username: userData.value.username,
  };
  await fetch(serverURL + "/api/users/" + userData.value._id, {
    method: "PATCH",
    body: JSON.stringify(data),
    headers: {
      'Authorization': 'Bearer '+ token,
      'Accept': 'application/json',
      'Content-Type': 'application/json;charset=utf-8'
  },
  })
    .then((res) => {
      if (!res.ok) {
        if (res.status === 400) {
          errorMessage.value = "نام کاربری تکراری است!"
          throw new Error("Uesrname Is Used!");
        } else {
          errorMessage.value = "مشکلی رخ داده است. دوباره تلاش کنید."
          throw new Error("An error has occurred. Please try again later.");
        }
      }
      return res.json();
    })
    .then((data) => {
      localStorage.setItem("username", userData.value.username);
      return data;
    });
  }

const setNewUsername = () => {
  if (phonenumberholder.value.classList.contains('hidden')) {
    IsSetNewUsername.value = false
    phonenumberholder.value.classList.remove('hidden');
    usernameholder.value.classList.remove('border-b-[2px]');
    usernameholder.value.classList.remove('justify-between');
    usernameholder.value.classList.remove('w-[200px]');
    usernameinput.value.classList.add('w-[100px]');
    usernameinput.value.disabled = true;

    
  } else {
    IsSetNewUsername.value = true
    phonenumberholder.value.classList.add('hidden');
    usernameholder.value.classList.add('border-b-[2px]');
    usernameholder.value.classList.add('justify-between');
    usernameholder.value.classList.add('w-[200px]');
    usernameinput.value.classList.remove('w-[100px]');
    usernameinput.value.disabled = false;
    usernameinput.value.focus();
  }
};


</script>
