  <div v-if="loading" class="absolute top-0 left-0 w-full bg-[#fffe] z-10">
    <div class="h-[90vh] flex flex-col gap-[10px] text-blue-500 items-center justify-center">
      <span class="text-[20px] font-bold">
         در حال ثبت تغییرات....
      </span>
    </div>
  </div>

  <div v-if="errorMessage" class="absolute top-0 left-0 w-full bg-[#fffe] z-10">
    <div class="h-[90vh] flex flex-col gap-[10px] text-red-500 items-center justify-center">
      <span class="text-[20px] font-bold">
        {{ errorMessage }}
      </span>
      <button class="bg-blue-500 rounded text-white w-[100px] px-2 p-1 text-center" @click="() => {errorMessage = null}">فهمیدم</button>
    </div>
  </div>

  <div v-if="successful" class="absolute top-0 left-0 w-full bg-[#fffe] z-10">
    <div class="h-[90vh] flex flex-col gap-[10px] text-blue-500 items-center justify-center">
      <span class="text-[20px] font-bold">
        تغییرات ذخیره شدند!
      </span>
      <button class="bg-blue-500 rounded text-white w-[100px] px-2 p-1 text-center" @click="() => {successful=false}">بستن</button>
    </div>
  </div>
</template>